import ProgressBar from "./Progress";

import './CSS/t.css';

import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';

function check_id(aray,val)
{
    var boolId = aray.some(  
        value => { return  value._id === val } ); 
return boolId;
}
function fixot(acc){ //function which take each object and put the credit and debit name and the values and the difference between the values
   var accob={}
   var clc,crval,mval = 0
   var crn,mn='';
   var accobarr=[]

acc.forEach((acco)=> {

   mn=acco.mname;
   crn=acco.dname;
   mval=acco.mvalue;
   crval=acco.dvalue;
   accob={mname:mn,mvalue:mval,cname:crn,cvalue:crval,res:(acco.mvalue>acco.dvalue)? clc= acco.mvalue-acco.dvalue:clc=acco.dvalue-acco.mvalue}
   accobarr.push(accob)
   });
   console.log(accobarr);
   return accobarr
}
function sum(ar){
    return ar.reduce(function(a, b){

        return a + b;

    }, 0);
}
function fixo(n)
{ 
    var accba={} 
    var dclc,cclc,crval,mval,resu= 0
    var crn,mn='';   var balarray=[]
    n.forEach((accb)=>{
     mn=accb._id;
     crn=accb._id;
     mval=accb.mbalance;

     crval=accb.cbalance;

     (accb.Result==null)?resu=0:resu=null;
   accba={mname:mn,mvalue:mval,cname:crn,cvalue:crval,res:resu}
    balarray.push(accba)

    });
    for(var i = 0;i<n.length-1;i++){
        mval+=n[i].mbalance;console.log(mval)
        crval+=n[i].cbalance;console.log(crval)

    }



       accba={mname:"Result",mvalue:mval,cname:"Result",cvalue:crval,res:"Result"+(mval>crval)?mval-crval:crval-mval}
       balarray.push(accba)
 return balarray
}

function compare(a, b) {
    if(a._id < b._id) { return -1; }
  else if(a._id > b._id) { return 1; }
  return 0;
  }
  function comparestr(a, b) {
    // Use toUpperCase() to ignore character casing
    const accA = a._id.toUpperCase();
    const accB = b._id.toUpperCase();

    let comparison = 0;
    if (accA > accB) {
      comparison = 1;
    } else if (accA < accB) {
      comparison = -1;
    }
    return comparison;
  }

  function search (key, inputdata){
   if(inputdata)
 return inputdata.find(o => o.name === key)
} 
export class BalanceStatement extends Component {

    constructor(props) {
        super(props);


        this.state = { credit: "",debit: "",daccn :null, caccn:null ,dvalue: null,cvalue: null ,accounts:[],d:[],c:[],db:[],dc:[],rows:[],

        balancestat:null,creditsum:null,debitsum:null,bool:"",
        columnDefs: [

            { headerName: "Debit Name", field: "mname",},
            { headerName: "Debit Balance", field: "mvalue", },
            { headerName: "Credit Name", field: "cname", },
            { headerName: "Credit Balance", field: "cvalue", },
            { headerName: "Result", field: "res", },




          ],

        };



         this.Assetaccount = [
            {id:101,name:"Bank/Cash at Bank"},
            {id:108, name:"Deferred Expense"},
            {id:110, name:"Others"},
            {id:112, name:"Accounts Receivable"},
            {id:157, name:"Equipment"},
            {id:130, name:"Prepaid Insurance"}

            ]

         this.Liabilityaccount = [
                {id:200,name:"Notes Payable"},
                {id:201, name:"Accounts Payable"},
                {id:209, name:"Unearned Service Revenue"},
                {id:230, name:"Interest Payable"},
                {id:231, name:"Deferred Gross profit"}

                ]
         this.Equity = [
            {id:300,name:"Owner Capital"},
            {id:311, name:"Share Capital-Ordinary"},
            {id:320, name:"Retained Earnings"},
            {id:330, name:"Capital contribtions"},
            {id:332, name:"Dividends"}
         ]

         const [value, setValue] = useState(0);



    }

    setaccNumber(name){
        var n=0;
        switch(name){
            case "Bank/Cash at Bank":

                n=101;
                break;
            case "Deferred Expense":

                n=108; 
                break;
            case "Others":
                n=110;
                break;
            case "Accounts Receivable":
                n=112;
                break; 
            case "Equipment":
                n=157; 
                break;
            case "Prepaid Insurance":
                n=130; 
                break;
            case "Notes Payable":
                n=200;
                break;
            case "Accounts Payable":
                n=201;
                break;
            case "Unearned Service Revenue":
                n=209;
                break;
            case "Interest Payable":
                n=230;
                break;
            case "Deferred Gross profit":
                n=231;
                break;

            case "Owner Capital":
                n=300;
                break;
            case "Share Capital-Ordinary":
                n=311;
                break;
            case "Retained Earnings":
                n=320; 
                break;
            case "Capital contribtions":
            n=330;
            break;
            case "Dividends":
            n=332;
            break;
            default:
            n=0;
            break;

        }
        return n;

}     

 calctrail(a,b){
    return((a>b)? 'Credit '+(a-b).toString() :(a===b)? '0':'Debit '+(b-a).toString() )
  }   
handleDebitAcc(e){
    this.setState({debit:e.target.value});
    this.setState({daccn:this.setaccNumber(e.target.value)});


    }
handleCreditAcc(e){
        this.setState({credit:e.target.value});
        this.setState({caccn:this.setaccNumber(e.target.value)});

    }

handledAccVal(e){
    this.setState({dvalue: e.target.value});
}

handlecAccVal(e){
    this.setState({cvalue: e.target.value});
}   

async ViewTrailBalance(e) {


        await axios.get('http://localhost:4000/getTrailBalance')
         .then((response) => {



             const data = response.data;



              const res= fixot(data)

              this.setState({balancestat:res});


            })
         .catch((err) => {
             console.log("Error retreiving data",err);
         });



    }

async activateBalance(){
   var m,n
  await  axios.get('http://localhost:4000/getBalance')
    .then((response) => {
        const data = response.data;
       m= fixo(data);
        console.log(data)
        this.setState({bs:data});
        this.setState({balancestat:m});
     console.log(this.state.bs)


       })
    .catch((err) => {
        console.log("Error retreiving data",err);
    });


}
resetstate(e){
    this.setState({balancestat:null});
    this.DisplayBalanceStatement.dd = null;
   this.AgGridReact = null
} 
async emptydata(e){


    await  axios.post('http://localhost:4000/emptydata')
    .then(() => {

     console.log("Data has been cleared")

       })
    .catch((err) => {
        console.log("Error Emptying data",err.res);
    });



}
handleSubmit(e) {


        e.preventDefault();

        let data = {
            "credit": this.state.credit,
            "cAccNo": this.state.caccn,
            "debit":this.state.debit,
            "dAccNo":this.state.daccn,
            "cvalue": this.state.cvalue,
            "dvalue": this.state.dvalue,

        }

      return fetch('http://localhost:4000/savedata', {

        method: 'POST',
        body: JSON.stringify(data),
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json;charset=UTF-8'
          },
    })
    .then(res => res.json())





    }


    DisplayBalanceStatement = (props) => {


    var u=[{}];
    var o=[{}];

    u =props.a1
    o=props.a2




const as = this.Assetaccount
const lia = this.Liabilityaccount
const q= this.Equity

  u.sort(compare);

var disdata=[]

if(this.state.bs)
var s= this.state.bs


var dd = null;
var  cc = null;
if(s && typeof s[1] != 'undefined'){
   dd =s[0].debitbook;
   cc = s[1].creditbook;

  }



if(dd)
{

    dd.forEach(element => 
        disdata.push(element)
        );


}
var acc2=[]



if(cc){
    cc.forEach(element => 
        disdata.push(element)
        );
}

  disdata.sort(comparestr)

var disdata2= disdata.filter(function(acc){
    if (acc.mbalance)
    {
        acc2.push(acc)


    }else if(acc.cbalance)
    {
        acc2.cname=acc._id;
        acc2.cbalance=acc.cbalance
    }
return null});
 if(dd && cc || this.state.balancestat){   return ( 
    <div className="ag-theme-alpine" style={ {height: '400px', width: '1000px'} }>
    <AgGridReact
        columnDefs={this.state.columnDefs}
        groupIncludeFooter={true}
        groupIncludeTotalFooter={true}
       rowData={this.state.balancestat}

                >
    </AgGridReact>

          </div>

    );
}else return null;






      }



    render() { 

        return (  
             <div>
        <br /><br />
              <section className="clean-block clean-hero" style={{backgroundImage: `url(${image})`  }}  >

              <div className="text"><p><font color='#800000'><b>Fill the balance Sheet</b></font></p>
            <form  >
            <div className="form-group">  <label htmlFor="exampleFormControlInput1"><font color='#800000'><b>Debit Account</b></font></label>
          <select className="form-control" id="name" onChange={this.handleDebitAcc.bind(this)}>
               <option>Choose</option>
               <optgroup label = "Assets">    
              { this.Assetaccount.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>
              <optgroup label = "Liabitility">    
              { this.Liabilityaccount.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>
              <optgroup label = "Equity">    
              { this.Equity.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>

          </select>
          <div><br /></div>
          <input type="text" onChange={this.handledAccVal.bind(this)} className="form-control" id="dvalue"  placeholder="Account Value"></input>          
            </div>

          <div className="form-group">  <label htmlFor="exampleFormControlInput1"><font color='#800000'><b>Credit Account</b></font></label>
          <select className="form-control" id="name" onChange={this.handleCreditAcc.bind(this)}>
          <option>Choose</option>
               <optgroup label = "Assets">    
              { this.Assetaccount.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>
              <optgroup label = "Liabitility">    
              { this.Liabilityaccount.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>
              <optgroup label = "Equity">    
              { this.Equity.map(acc =>  <option key={acc.id}> {acc.name}</option>)}
              </optgroup>
          </select>   
          <div><br /></div>
          <input type="text" onChange={this.handlecAccVal.bind(this)} className="form-control" id="cvalue" placeholder="Account Value"></input>        
            </div>


            <button type="submit" onClick={this.handleSubmit.bind(this)}  className="btn btn-primary mb-2"> Confirm</button>&nbsp;&nbsp;&nbsp;&nbsp;
            <button type="reset"  onClick={this.resetstate.bind(this)} className="btn btn-primary mb-2">Reset</button>&nbsp;&nbsp;&nbsp;&nbsp;
            <button type="reset"  onClick={this.emptydata.bind(this)} className="btn btn-primary mb-2">Reset Database</button>


            </form>
           <div> 
               <br />
            <button type="submit" onClick={this.ViewTrailBalance.bind(this)}  className="btn btn-primary mb-2"> View Trail Balance </button>
            &nbsp; &nbsp;
            <button type="submit" onClick={this.activateBalance.bind(this)}  className="btn btn-primary mb-2"> View Balance Sheet</button>
            <p>Data will be fetched in a second automatically.</p>
       </div>
          <div>   





          </div>
         <div><this.DisplayBalanceStatement a1={this.state.d} a2={this.state.c}/></div>          

                </div>





        </section>
        <section className="clean-block features">
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-5 feature-box">
                        <i className="icon-star icon"></i>
                        <h4>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Balance Sheet</h4>
                        <p>&nbsp;financial statement that reports a company's assets, liabilities and shareholders' equity.</p>
